import React from 'react';

function PopupWithForm(props) {
    const { title, name, isOpen, onClose } = props;

    return (
        <div 
        className={`popup popup-${name} ${isOpen && 'popup_opened'}`}
        onClick={onClose}
        >

            <button className={`button-close popup__cross popup__cross_btn_close-${props.name} popup__button-close`}
                type="button"></button>
            <form id="popup__form" className={`popup__form popup__form_${name}`} method="get" name={`${name}-form`} novalidate>

                <h2 className="popup__title">{title}</h2>


                <input id="popup__input_type_name" className="popup__input popup__input_type_name" type="text" name="name"
                    value="Жак-Ив Кусто" minLength="2" maxLength="40" required />
                <span id="popup__input_type_name-error" className="popup__error"></span>

                <input id="popup__input_type_about" className="popup__input popup__input_type_about" type="text" name="about"
                    value="Исследователь океана" minLength="2" maxLength="200" required />
                <span id="popup__input_type_about-error" className="popup__error"></span>



                <button class={`popup__button popup__save popup__save-${name}`} type="submit">Сохранить</button>
            </form>
        </div>
    )
}

export default PopupWithForm
