import React from 'react';
import { api } from './../utils/Api';

function Main(props) {
    const { onEditAvatar, onEditProfile, onAddPlace } = props;
    const [userName, setUserName] = React.useState([]);
    const [userDescription, setUserDescription] = React.useState([]);
    const [userAvatar, setUserAvatar] = React.useState([]);
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getUserData()
            .then((res) => {
                setUserName(res.name);
                setUserDescription(res.about);
                setUserAvatar(res.avatar);
            })
            .catch((err) => {
                console.log(err);
            })
    }, []);


    React.useEffect(() => {
        api.getInitialCards()
            .then((cards) => {
                console.log(cards)
            })
    })

    return (
        <main>
            <section className="profile">
                <div className="profile__container">
                    <div className="profile__photo-wrapper" onClick={onEditAvatar}>
                        <img name="avatar"
                            src={userAvatar}
                            alt="Ваше фото" className="profile__photo" />
                    </div>
                    <div className="profile__info">
                        <h1 className="profile__name">{userName}</h1>
                        <button className="profile__btn-edit" type="button" value="" onClick={onEditProfile}></button>
                        <p className="profile__about">{userDescription}</p>
                    </div>
                    <button className="profile__btn-add" type="button" onClick={onAddPlace}></button>
                </div>
            </section>

            <section className="photos">
                <article className="photo">
                    <div className="photo__image-container">
                        <button className="photo__trash" aria-label="Удалить" type="button"></button>
                        <img className="photo__image photo__image_open" src="" alt="" name="name" />
                    </div>
                    <h3 className="photo__title" name="link"></h3>
                    <div className="photo__like-container">
                        <button className="photo__like" type="button" aria-label="Нравится"></button>
                        <p className="photo__like-counter"></p>
                    </div>
                </article>
            </section>
        </main>
    )
}

export default Main
