            <div className="popup popup-delete">
                <button className="button-close popup__cross popup__cross_btn_close-photo popup__button-close"
                    type="button"></button>
                <form className="popup__form popup__form_delete" name="delete-form">
                    <h2 className="popup__title popup__title-delete">Вы уверены?</h2>
                    <button className="popup__button popup__save popup__save-delete" type="submit">Да</button>
                </form>
            </div>

            <div className="popup popup-avatar">
                <button className="button-close popup__cross popup__cross_btn_close-photo popup__button-close" type="button"></button>
                <form className="popup__form popup__form_avatar" name="avatar-form">
                    <h2 className="popup__title">Обновить аватар</h2>

                    <input id="popup__input_type_avatar" className="popup__input popup__input_type_avatar" name="link"
                        value="" placeholder="Ссылка на картинку" type="url" required />
                    <span id="popup__input_type_avatar-error" className="popup__error"></span>

                    <button className="popup__button popup__save" type="submit">Сохранить</button>
                </form>
            </div>

        </body>
    );
}

export default App;
