import React from 'react';
import PlacePopup from './PlacePopup';
import ProfilePopup from './ProfilePopup';
import AvatarPopup from './AvatarPopup';
import DeletePopup from './DeletePopup';

function PopupWithForm(props) {
    const { title, name, buttonText, isOpen, onClose } = props;

    return (
        <div
            className={`popup popup-${name} ${isOpen && 'popup_opened'}`}
            onClick={onClose}>

            <button className={`button-close popup__cross popup__cross_btn_close-${name}`}
                type="button"></button>
            <form id="popup__form" className={`popup__form popup__form_${name}`} method="get" name={`${name}-form`} noValidate>

                <AvatarPopup 
                name={name}
                title={title}
                buttonText={buttonText}
                />

                <ProfilePopup 
                name={name}
                title={title}
                buttonText={buttonText}
                />

                <PlacePopup 
                name={name}
                title={title}
                />

                <DeletePopup 
                name={name}
                title={title}
                />

            </form>

        </div>
    )
}

export default PopupWithForm
